$(".container #submit").click(function(){var a=$("#username").val();var d=$("#password").val();var c=$("#email01").val();var b=$("#birth01").val();console.log(a,d,c,b);$.ajax({type:"POST",url:"data/userReg.php",data:{uname:a,upwd:d,uemail:c,ubirthday:b},success:function(e,h,g){if(e=="succ"){$(".container").html("注册成功！！！");var f=$("<a href='index.html'>点击跳转到首页</a>");$(".container").append(f)}else{if(e=="blank"){alert("用户名或密码不能为空！")}}}})});$(".container #username").blur(function(){var a=$(this).val();var b=$(this).parent().next();$.ajax({type:"POST",url:"data/unameCheck.php",data:{uname:a},success:function(c,e,d){if(c=="uname exists"){b.html("用户名已存在！！！")}else{if(c=="succ"){b.html("用户验证通过！")}}}})});